mapper.insert:

insert into cm_users (id, job_num, 
  user_name, user_nick_name, head_pic,tel_num,
  address, create_time, update_time, 
  del_time, del_flag, remark,wechat_number
  )
values (#{id,jdbcType=BIGINT}, #{jobNum,jdbcType=VARCHAR}, 
  #{userName,jdbcType=VARCHAR}, #{userNickName,jdbcType=VARCHAR}, #{headPic,jdbcType=VARCHAR},#{telNum,jdbcType=VARCHAR}, 
  #{address,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, 
  #{delTime,jdbcType=TIMESTAMP}, #{delFlag,jdbcType=CHAR}, #{remark,jdbcType=VARCHAR},#{wechatNumber,jdbcType=VARCHAR}
  )

mapper.select:
SELECT u.*,p.prize_name as prizeName,
pl.prize_level_name as prizeLevelName FROM cm_users u
LEFT JOIN cm_prize_record r ON u.id = r.user_id
LEFT JOIN cm_prize p ON p.id = r.prize_id
LEFT JOIN cm_prize_level pl ON pl.id = p.prize_level_id


		pl.id = #{usersVo.prizeLevelId} and pl.del_flag = '0' and p.del_flag = '0'


		and u.user_name = #{usersVo.userName}
		and u.del_flag ='0'


		and u.job_num = #{usersVo.jobNum}


order by u.id asc limit #{pageRequest.page},#{pageRequest.pageSize}
